ecc_agent_script_include.script:
associateTags();

function associateTags(){
	var vmwareApi = new VMWareRestAPI(this.parameters, this.headers);
	var resourceId = this.parameters.get('ResourceID');
	var tagsJSON = this.parameters.get('Tags');
	var resourceType = this.parameters.get('ResourceType');
	var createCategories = false; // not currently implemented categories will not be created
	var createTags = false; // not currently implemented tags will not be created
// 	var resourceId = '';
// 	var tagsJSON = '';

	var response = vmwareApi.assignTags(resourceId,tagsJSON,createCategories,createTags,resourceType);
	return response;
}
